[c]
/*
ID: lmy0525
LANG: C++
PROG: beads
*/
#include &lt;iostream&gt;
#include &lt;fstream&gt;
#include &lt;string&gt;

using namespace std;

int main()
{
    ofstream fout (&quot;beads.out&quot;);
    ifstream fin (&quot;beads.in&quot;);
    int N; // 3 - 350
    string necklace;
    string necklace3;
    fin &gt;&gt; N &gt;&gt; necklace;
    necklace3 = necklace + necklace + necklace;
    int res = 0;
    for (int i = N; i &lt; 2*N; i++)
    {
        int cnt1 = 0;
        char color1 = necklace3[i-1];
        for (int j = i-1; j &gt; i-1-N; j--)
        {
            if (necklace3[j] == 'w')
            {
                cnt1++;
            }
            else
            {
                if (color1 == 'w')
                {
                    color1 = necklace3[j];
                    cnt1++;
                }
                else if (color1 == necklace3[j])
                {
                    cnt1++;
                }
                else
                {
                    break;
                }
            }
        }
        char color2 = necklace3[i];
        int cnt2 = 0;
        for (int j = i; j &lt; i+N; j++)
        {
            if (necklace3[j] == 'w')
            {
                cnt2++;
            }
            else
            {
                if (color2 == 'w')
                {
                    color2 = necklace3[j];
                    cnt2++;
                }
                else if (color2 == necklace3[j])
                {
                    cnt2++;
                }
                else
                {
                    break;
                }
            }
        }
        int cnt;
        if (cnt1 + cnt2 &gt; N)
            cnt = N;
        else
            cnt = cnt1+cnt2;
        res = max(cnt, res);
    }
    fout &lt;&lt; res &lt;&lt; endl;
    return 0;
}
