标程：

[c]
#include &lt;stdio.h&gt;
#include &lt;stdlib.h&gt;
#include &lt;string.h&gt;
#include &lt;assert.h&gt;

#define MAXSTALL 200
int hascow[MAXSTALL];

int
intcmp(const void *va, const void *vb)
{
	return *(int*)vb - *(int*)va;
}

void
main(void)
{
    FILE *fin, *fout;
    int n, m, nstall, ncow, i, j, c, lo, hi, nrun;
    int run[MAXSTALL];

    fin = fopen(&quot;barn1.in&quot;, &quot;r&quot;);
    fout = fopen(&quot;barn1.out&quot;, &quot;w&quot;);

    assert(fin != NULL &amp;&amp; fout != NULL);

    fscanf(fin, &quot;%d %d %d&quot;, &amp;m, &amp;nstall, &amp;ncow);
    for(i=0; i&lt;ncow; i++) {
	fscanf(fin, &quot;%d&quot;, &amp;c);
	hascow[c-1] = 1;
    }

    n = 0;	/* answer: no. of uncovered stalls */

    /* count empty stalls on left */
    for(i=0; i&lt;nstall &amp;&amp; !hascow[i]; i++)
	n++;
    lo = i;

    /* count empty stalls on right */
    for(i=nstall-1; i&gt;=0 &amp;&amp; !hascow[i]; i--)
	n++;
    hi = i+1;

    /* count runs of empty stalls */
    nrun = 0;
    i = lo;
    while(i &lt; hi) {
	while(hascow[i] &amp;&amp; i&lt;hi)
	    i++;

	for(j=i; j&lt;hi &amp;&amp; !hascow[j]; j++)
	    ;

	run[nrun++] = j-i;
	i = j;
    }

    /* sort list of runs */
    qsort(run, nrun, sizeof(run[0]), intcmp);

    /* uncover best m-1 runs */
    for(i=0; i&lt;nrun &amp;&amp; i&lt;m-1; i++)
	n += run[i];

    fprintf(fout, &quot;%d\n&quot;, nstall-n);
    exit(0);
}
[/c]

思路基本一致，不过标程中计算左右空格栏有点没必要。

另外一个：

[pas]
var f:text;
    a,b:array[1..1000] of longint;
    i,m,s,c,k:longint;

procedure qsort(l,r:longint);
    var i,j,x,y:longint;
begin
     i:=l; j:=r; x:=a[(l+r) div 2];
     repeat
           while a[i]&lt;x do i:=i+1;
           while x&lt;a[j] do j:=j-1;
           if i&lt;=j then begin
                y:=a[i]; a[i]:=a[j]; a[j]:=y;
                i:=i+1;
                j:=j-1;
           end;
     until i&gt;j;
     if l&lt;j then qsort(l,j);
     if i&lt;r then qsort(i,r);
end;

procedure qsortb(l,r:longint);
    var i,j,x,y:longint;
begin
     i:=l; j:=r; x:=b[(l+r) div 2];
     repeat
           while b[i]&lt;x do i:=i+1;
           while x&lt;b[j] do j:=j-1;
           if i&lt;=j then
           begin
                y:=b[i]; b[i]:=b[j]; b[j]:=y;
                i:=i+1;
                j:=j-1;
           end;
     until i&gt;j;
     if l&lt;j then qsortb(l,j);
     if i&lt;r then qsortb(i,r);
end;

begin
     assign(f,'barn1.in');
     reset(f);
     readln(f,m,k,c);
     for i:=1 to c do readln(f,a[i]);
     qsort(1,c);
     for i:=1 to c-1 do b[i]:=a[i+1]-a[i]-1;
     qsortb(1,c-1);
     for i:=c-1 downto (c-m+1) do s:=s+b[i];
     close(f);
     assign(f,'barn1.out');
     rewrite(f);
     writeln(f,a[c]-a[1]-s+1);
     close(f);
end.
[/pas]

这个结构挺清晰。

刚开始超时了一次，因为在搜索某个位置的最长回文时，每次都进行了一次substr操作，导致超时，最后改成了找到了最长回文时，才进行一次substr。<!--more-->

[c]
/*
ID: lmy0525
PROG: calfflac
LANG: C++
*/
#include &lt;iostream&gt;
#include &lt;fstream&gt;
#include &lt;string&gt;

using namespace std;

string s;
int len = 0;
string str = &quot;&quot;;

void calc()
{
    for (int pos = 0; pos &lt; s.size(); pos++)
    {
        if (isalpha(s[pos]))
        {
            int length = 1;
            int l=pos, r=pos;
            for (int left = pos-1, right = pos+1; (right-left+1)&lt;=2000 &amp;&amp; left&gt;=0 &amp;&amp; right &lt; s.size(); left--, right++)
            {
                while( left &gt;= 0 &amp;&amp; !isalpha(s[left]) ) left--;
                while( right &lt; s.size() &amp;&amp; !isalpha(s[right]) ) right++;
                if ( left == -1 || right == s.size() ) break;
                if ( toupper(s[left]) == toupper(s[right]) )
                {
                    l = left, r = right, length += 2;
                }
                else
                {
                    break;
                }
            }
            if (length &gt; len)
            {
                len = length;
                str = s.substr(l, r-l+1);
            }
            length = 0;
            for (int left = pos, right = pos+1; (right-left+1)&lt;=2000 &amp;&amp; left&gt;=0 &amp;&amp; right &lt; s.size(); left--, right++)
            {
                while( left &gt;= 0 &amp;&amp; !isalpha(s[left]) ) left--;
                while( right &lt; s.size() &amp;&amp; !isalpha(s[right]) ) right++;
                if ( left == -1 || right == s.size() ) break;
                if ( toupper(s[left]) == toupper(s[right]) )
                {
                    l = left, r = right, length += 2;
                }
                else
                {
                    break;
                }
            }
            if (length &gt; len)
            {
                len = length;
                str = s.substr(l, r-l+1);
            }
        }
    }
}

int main()
{
    ofstream fout (&quot;calfflac.out&quot;);
    ifstream fin (&quot;calfflac.in&quot;);
    string temp;
    while(getline(fin, temp))
    {
        s += temp+&quot;\n&quot;;
    }
    calc();
    fout &lt;&lt; len &lt;&lt; endl;
    fout &lt;&lt; str &lt;&lt; endl;
    return 0;
}
