这题竟然有HINTS。。。不过写出来和任何一个标程都不太一样。
标程：

[c]
#include &lt;stdio.h&gt;
#include &lt;string.h&gt;
#include &lt;assert.h&gt;
#include &lt;stdlib.h&gt;

FILE *fout;
long a, b;

int
isprime(long n)
{
    long i;

    if(n == 2)
	return 1;

    if(n%2 == 0)
	return 0;

    for(i=3; i*i &lt;= n; i+=2)
	if(n%i == 0)
	    return 0;

    return 1;
}

void
gen(int i, int isodd)
{
    char buf[30];
    char *p, *q;
    long n;

    sprintf(buf, &quot;%d&quot;, i);

    p = buf+strlen(buf);
    q = p - isodd;

    while(q &gt; buf)
	*p++ = *--q;
    *p = '&#92;&#48;';

    n = atol(buf);
    if(a &lt;= n &amp;&amp; n &lt;= b &amp;&amp; isprime(n))
	fprintf(fout, &quot;%ld\n&quot;, n);
}

void
genoddeven(int lo, int hi)
{
    int i;

    for(i=lo; i&lt;=hi; i++)
        gen(i, 1);

    for(i=lo; i&lt;=hi; i++)
        gen(i, 0);
}

void
generate(void)
{
    genoddeven(1, 9);
    genoddeven(10, 99);
    genoddeven(100, 999);
    genoddeven(1000, 9999);
}

void
main(void)
{
    FILE *fin;

    fin = fopen(&quot;pprime.in&quot;, &quot;r&quot;);
    fout = fopen(&quot;pprime.out&quot;, &quot;w&quot;);
    assert(fin != NULL &amp;&amp; fout != NULL);

    fscanf(fin, &quot;%ld %ld&quot;, &amp;a, &amp;b);

    generate();
    exit (0);
}

