[c]
/*
ID: lmy0525
PROG: preface
LANG: C++
*/
#include &lt;iostream&gt;
#include &lt;fstream&gt;
#include &lt;string&gt;
#include &lt;algorithm&gt;
#include &lt;cmath&gt;
#include &lt;vector&gt;

using namespace std;

ofstream fout;
ifstream fin;

char c[7] = {'I', 'V', 'X', 'L', 'C', 'D', 'M'};
int cnt[7]; // I V X L C D M

void calc(int n)
{
    // M MM MMM
    cnt[6] += n / 1000;
    n %= 1000;

    if ( n &gt;= 900 ) // CM
    {
        cnt[6]++;
        cnt[4]++;
        n -= 900;
    }
    else if (n &gt;= 500) // D DC DCC DCCC
    {
        cnt[5]++;
        cnt[4] += (n-500) / 100;
        n = (n-500) % 100;
    }
    else if (n &gt;= 400) // CD
    {
        cnt[4]++;
        cnt[5]++;
        n -= 400;
    }
    else if (n &gt;= 100) // C CC CCC
    {
        cnt[4] += n / 100;
        n %= 100;
    }

    if ( n &gt;= 90 ) // XC
    {
        cnt[4]++;
        cnt[2]++;
        n -= 90;
    }
    else if (n &gt;= 50) // L LX LXX LXXX
    {
        cnt[3]++;
        cnt[2] += (n-50) / 10;
        n = (n-50) % 10;
    }
    else if (n &gt;= 40) // XL
    {
        cnt[2]++;
        cnt[3]++;
        n -= 40;
    }
    else if (n &gt;= 10) // X XX XXX
    {
        cnt[2] += n / 10;
        n %= 10;
    }

    if ( n &gt;= 9 ) // IX 
    {
        cnt[2]++;
        cnt[0]++;
    }
    else if (n &gt;= 5) // V VI VII VIII
    {
        cnt[1]++;
        cnt[0] += n-5;
    }
    else if (n &gt;= 4) // IV
    {
        cnt[0]++;
        cnt[1]++;
    }
    else if (n &gt;= 1) // I II III 
    {
        cnt[0] += n;
    }
}

int main() {
    fout.open(&quot;preface.out&quot;);
    fin.open(&quot;preface.in&quot;);
    int N;
    fin &gt;&gt; N;
    for (int i = 1; i &lt;= N; i++)
    {
        calc(i);
    }
    for (int i = 0; i &lt; 7; i++)
    {
        if (cnt[i])
        {
            fout &lt;&lt; c[i] &lt;&lt; &quot; &quot; &lt;&lt; cnt[i] &lt;&lt; endl;
        }
    }
    return 0;
}
