[c]
/*
ID: lmy0525
PROG: runround
LANG: C++
*/
#include &lt;iostream&gt;
#include &lt;fstream&gt;
#include &lt;string&gt;
#include &lt;algorithm&gt;
#include &lt;cmath&gt;
#include &lt;vector&gt;
#include &lt;cstring&gt;

using namespace std;

ofstream fout;
ifstream fin;

int M;
char num[10];
bool used[10];
bool done;

bool isRun(char* n)
{
    char temp[10];
    strcpy(temp, n);
    int len = strlen(n);

    int i = 0;
    while(temp[i] != '*')
    {
        temp[i] = '*';
        i = (i + n[i] - '0') % len;
    }

    if (i != 0) return false;

    for (int i = 0; i &lt; len; i++)
    {
        if(temp[i] != '*') return false;
    }

    return true;
}

void generate(int cur, int n)
{
    if (done) return;

    if (cur == n)
    {
        // check
        num[cur] = '&#92;&#48;';
        int temp = atoi(num);
        if (temp &gt; M &amp;&amp; isRun(num))
        {
            fout &lt;&lt; temp &lt;&lt; endl;
            done = true;
        }
    }

    for (int i = 1; i &lt; 10; i++)
    {
        if (!used[i])
        {
            used[i] = true;

            num[cur] = i+'0';
            generate(cur+1, n);

            used[i] = false;
        }
    }
}

int main() {
    fout.open(&quot;runround.out&quot;);
    fin.open(&quot;runround.in&quot;);
    fin &gt;&gt; M;
    for (int i = 1; i &lt; 10; i++) // 1-9
    {
        generate(0, i);
    }
    return 0;
}
