#include &lt;iostream&gt;
#include &lt;fstream&gt;
#include &lt;string&gt;
#include &lt;algorithm&gt;
#include &lt;cmath&gt;
#include &lt;vector&gt;

using namespace std;

ofstream fout;
ifstream fin;

int N, C;
vector &lt;int&gt; on, off;
int status[4];
int d[4][2] = { {1, 1}, {1, 2}, {2, 2}, {1, 3} };
vector &lt;string&gt; res;

void generate (int cur)
{
    if (cur == 4)
    {
        int ones = 0;
        int lamps[101];
        for (int i = 1; i &lt;= N; i++) lamps[i] = 1;
        // turn the lamps
        for (int i = 0; i &lt; 4; i++)
        {
            if (status[i] == 1)
            {
                ones++;
                for (int j = d[i][0]; j &lt;= N; j += d[i][1])
                {
                    lamps[j] = 1-lamps[j];
                }
            }
        }
        // check config    
        for (int i = 0; i &lt; on.size(); i++)
        {
            if (lamps[on[i]] != 1) return;
        }
        for (int i = 0; i &lt; off.size(); i++)
        {
            if (lamps[off[i]] != 0) return;
        }
        // check C
        if (C &lt; ones) return;
        if (C%2 != ones%2) return;

        string temp = &quot;&quot;;
        for (int i = 1; i &lt;= N; i++)
            temp += lamps[i]+'0';
        res.push_back(temp);  
        return;
    }

    status[cur] = 0;
    generate(cur+1);

    status[cur] = 1;
    generate(cur+1);
}

int main() {
    fout.open(&quot;lamps.out&quot;);
    fin.open(&quot;lamps.in&quot;);
    fin &gt;&gt; N &gt;&gt; C;
    int temp;
    fin &gt;&gt; temp;
    while (temp != -1)
    {
        on.push_back(temp);
        fin &gt;&gt; temp;
    }
    fin &gt;&gt; temp;
    while (temp != -1)
    {
        off.push_back(temp);
        fin &gt;&gt; temp;
    }
    generate(0);
    sort(res.begin(), res.end());
    unique(res.begin(), res.end());
    for (int i = 0; i &lt; res.size(); i++)
        fout &lt;&lt; res[i] &lt;&lt; endl;
    if (res.size() == 0)
        fout &lt;&lt; &quot;IMPOSSIBLE&quot; &lt;&lt; endl;
    return 0;
}
