[c]
/*
ID: lmy0525
PROG: zerosum
LANG: C++
*/
#include &lt;iostream&gt;
#include &lt;fstream&gt;
#include &lt;string&gt;
#include &lt;algorithm&gt;
#include &lt;cmath&gt;
#include &lt;vector&gt;

using namespace std;

char op[8];

ofstream fout;
ifstream fin;

void generate(int cur, int n)
{
    if (cur == n)
    {
        // calc
        int a = 1, b = 0;
        char op_pre;
        for (int i = 0; i &lt; n; i++)
        {
            if (b == 0)
            {
                if (op[i] == ' ')
                {
                    a = a*10 + (i+2);
                }
                else
                {
                    b = i+2; 
                    op_pre = op[i];
                }
            }
            else
            {
                if (op[i] == ' ')
                {
                    b = b*10+(i+2);
                }
                else
                {
                    if (op_pre == '+') a += b;
                    else a -= b;

                    b = i+2;
                    op_pre = op[i];
                }
            }
        }
        // last
        if (op_pre == '+') a += b;
        else a -= b;

        if (a == 0)
        {
            fout &lt;&lt; 1;
            for (int i = 0; i &lt; n; i++)
            {
                fout &lt;&lt; op[i] &lt;&lt; i+2;
            }
            fout &lt;&lt; endl;
        }
        return;
    }

    op[cur] = ' ';
    generate(cur+1, n);

    op[cur] = '+';
    generate(cur+1, n);

    op[cur] = '-';
    generate(cur+1, n);
}

int main() {
    fout.open(&quot;zerosum.out&quot;);
    fin.open(&quot;zerosum.in&quot;);
    int N;
    fin &gt;&gt; N;
    generate(0, N-1);
    return 0;
}
