这个题在TEXT Flood Fill Algorithms中分析过，用DFS搞定了

代码：<!--more-->
[c]
/*
ID: lmy0525
PROG: concom
LANG: C++
*/
#include &lt;iostream&gt;
#include &lt;fstream&gt;
#include &lt;string&gt;
#include &lt;algorithm&gt;
#include &lt;cmath&gt;
#include &lt;vector&gt;
#include &lt;cstring&gt;

using namespace std;

ofstream fout;
ifstream fin;

vector &lt; pair&lt;int, int&gt; &gt; owns[101];
int per[101];
int color[101];

void dfs(int n)
{
    for (int i = 0; i &lt; owns[n].size(); i++)
    {
        int v = owns[n][i].first;
        int p = owns[n][i].second;
        per[v] += p;
        if (per[v] &gt; 50 &amp;&amp; color[v] == 0)
        {
            color[v] = 1;
            dfs(v);
        }
    }
}

int main()
{
    fout.open(&quot;concom.out&quot;);
    fin.open(&quot;concom.in&quot;);
    int n;
    fin &gt;&gt; n;
    for (int i = 0; i &lt; n; i++)
    {
        int a, b, p;
        fin &gt;&gt; a &gt;&gt; b &gt;&gt; p;
        owns[a].push_back(make_pair(b, p));
    }
    for (int i = 1; i &lt;= 100; i++)
    {
        memset(color, 0, sizeof(color));
        memset(per, 0, sizeof(per));
        dfs(i);
        for (int j = 1; j &lt;= 100; j++)
        {
            if (i == j) continue;
            if (per[j] &gt; 50) fout &lt;&lt; i &lt;&lt; &quot; &quot; &lt;&lt; j &lt;&lt; endl;
        }
    }
    return 0;
}
[/c]

标程：
[c]
#include &lt;stdio.h&gt;
#include &lt;stdlib.h&gt;
#include &lt;string.h&gt;
#include &lt;assert.h&gt;

#define NCOM 101

int owns[NCOM][NCOM];        /* [i,j]: how much of j do i and its
                                controlled companies own? */
int controls[NCOM][NCOM];    /* [i, j]: does i control j? */

/* update info: now i controls j */
void
addcontroller(int i, int j)
{
    int k;

    if(controls[i][j])        /* already knew that */
        return;

    controls[i][j] = 1;

    /* now that i controls j, add to i's holdings everything from j */
    for(k=0; k&lt;NCOM; k++)
        owns[i][k] += owns[j][k];

    /* record the fact that controllers of i now control j */
    for(k=0; k&lt;NCOM; k++)
        if(controls[k][i])
            addcontroller(k, j);

    /* if i now controls more companies, record that fact */
    for(k=0; k&lt;NCOM; k++)
        if(owns[i][k] &gt; 50)
            addcontroller(i, k);
}

/* update info: i owns p% of j */
void
addowner(int i, int j, int p)
{
    int k;

    /* add p% of j to each controller of i */
    for(k=0; k&lt;NCOM; k++)
        if(controls[k][i])
            owns[k][j] += p;

    /* look for new controllers of j */
    for(k=0; k&lt;NCOM; k++)
        if(owns[k][j] &gt; 50)
            addcontroller(k, j);
}

void
main(void)
{
    FILE *fin, *fout;
    int i, j, n, a, b, p;

    fin = fopen(&quot;concom.in&quot;, &quot;r&quot;);
    fout = fopen(&quot;concom.out&quot;, &quot;w&quot;);
    assert(fin != NULL &amp;&amp; fout != NULL);

    for(i=0; i&lt;NCOM; i++)
        controls[i][i] = 1;

    fscanf(fin, &quot;%d&quot;, &amp;n);
    for(i=0; i&lt;n; i++) {
        fscanf(fin, &quot;%d %d %d&quot;, &amp;a, &amp;b, &amp;p);
        addowner(a, b, p);
    }

    for(i=0; i&lt;NCOM; i++)
    for(j=0; j&lt;NCOM; j++)
        if(i != j &amp;&amp; controls[i][j])
            fprintf(fout, &quot;%d %d\n&quot;, i, j);
    exit(0);
}
[/c]

模拟题，模拟的步数开小了，竟然给过了

代码：<!--more-->
[c]
/*
ID: lmy0525
PROG: ttwo
LANG: C++
*/
#include &lt;iostream&gt;
#include &lt;fstream&gt;
#include &lt;string&gt;
#include &lt;algorithm&gt;
#include &lt;cmath&gt;
#include &lt;vector&gt;
#include &lt;cstring&gt;

using namespace std;

ofstream fout;
ifstream fin;

string grid[10];
int fr, fc, cr, cc;
int d[4][2] = { {-1, 0}, {0, 1}, {1, 0}, {0, -1} }; // N E S W
int df, dc;

int main()
{
    fout.open(&quot;ttwo.out&quot;);
    fin.open(&quot;ttwo.in&quot;);
    for (int i = 0; i &lt; 10; i++)
    {
        fin &gt;&gt; grid[i];
    }
    for (int i = 0; i &lt; 10; i++)
    {
        for (int j = 0; j &lt; 10; j++)
        {
            if (grid[i][j] == 'F')
            {
                fr = i;
                fc = j;
            }
            if (grid[i][j] == 'C')
            {
                cr = i;
                cc = j;
            }
        }
    }

    bool flag = false;
    int min;
    for (int i = 1; i &lt;= 1000; i++)
    {
        // Farmer
        int nextfr = fr + d[df][0];
        int nextfc = fc + d[df][1];
        if (nextfr &lt; 0 || nextfr &gt; 9 || nextfc &lt; 0 || nextfc &gt; 9 || grid[nextfr][nextfc] == '*')
        {
            df = (df+1)%4;
        }
        else
        {
            fr = nextfr;
            fc = nextfc;
        }
        // Cows
        int nextcr = cr + d[dc][0];
        int nextcc = cc + d[dc][1];
        if (nextcr &lt; 0 || nextcr &gt; 9 || nextcc &lt; 0 || nextcc &gt; 9 || grid[nextcr][nextcc] == '*')
        {
            dc = (dc+1)%4;
        }
        else
        {
            cr = nextcr;
            cc = nextcc;
        }
        // meet?
        if (fr == cr &amp;&amp; fc == cc)
        {
            flag = true;
            min = i;
            break;
        }
    }
    if (flag) fout &lt;&lt; min &lt;&lt; endl;
    else fout &lt;&lt; 0 &lt;&lt; endl;
    return 0;
}
[/c]

标程：
[c]
/*
PROG: ttwo
ID: rsc001
*/

#include &lt;stdio.h&gt;
#include &lt;stdlib.h&gt;
#include &lt;string.h&gt;
#include &lt;assert.h&gt;

char grid[10][10];

/* delta x, delta y position for moving north, east, south, west */
int deltax[] = { 0, 1, 0, -1 };
int deltay[] = { -1, 0, 1, 0 };

void
move(int *x, int *y, int *dir)
{
	int nx, ny;

	nx = *x+deltax[*dir];
	ny = *y+deltay[*dir];

	if(nx &lt; 0 || nx &gt;= 10 || ny &lt; 0 || ny &gt;= 10 || grid[ny][nx] == '*')
		*dir = (*dir + 1) % 4;
	else {
		*x = nx;
		*y = ny;
	}
}

void
main(void)
{
	FILE *fin, *fout;
	char buf[100];
	int i, x, y;
	int cowx, cowy, johnx, johny, cowdir, johndir;


	fin = fopen(&quot;ttwo.in&quot;, &quot;r&quot;);
	fout = fopen(&quot;ttwo.out&quot;, &quot;w&quot;);
	assert(fin != NULL &amp;&amp; fout != NULL);

	cowx = cowy = johnx = johny = -1;

	for(y=0; y&lt;10; y++) {
		fgets(buf, sizeof buf, fin);
		for(x=0; x&lt;10; x++) {
			grid[y][x] = buf[x];
			if(buf[x] == 'C') {
				cowx = x;
				cowy = y;
				grid[y][x] = '.';
			}
			if(buf[x] == 'F') {
				johnx = x;
				johny = y;
				grid[y][x] = '.';
			}
		}
	}

	assert(cowx &gt;= 0 &amp;&amp; cowy &gt;= 0 &amp;&amp; johnx &gt;= 0 &amp;&amp; johny &gt;= 0);

	cowdir = johndir = 0;	/* north */

	for(i=0; i&lt;160000 &amp;&amp; (cowx != johnx || cowy != johny); i++) {
		move(&amp;cowx, &amp;cowy, &amp;cowdir);
		move(&amp;johnx, &amp;johny, &amp;johndir);
	}

	if(cowx == johnx &amp;&amp; cowy == johny)
		fprintf(fout, &quot;%d\n&quot;, i);
	else
		fprintf(fout, &quot;0\n&quot;);
	exit(0);
}
[/c]

在两个出口出进行2次BFS，记录下每个点离最近出口的距离，最后找到一个离出口最远的点的距离。

代码：<!--more-->
[c]
/*
ID: lmy0525
PROG: maze1
LANG: C++
*/
#include &lt;iostream&gt;
#include &lt;fstream&gt;
#include &lt;string&gt;
#include &lt;algorithm&gt;
#include &lt;cmath&gt;
#include &lt;vector&gt;
#include &lt;cstring&gt;
#include &lt;queue&gt;

using namespace std;

ofstream fout;
ifstream fin;

string maze[201];

int W, H;
int lines, width;
int color[201][77];
int d[201][77];
int dir[4][2] = { {-2, 0}, {2, 0}, {0, -2}, {0, 2} };
int out[201][77];

void bfs (int r, int c)
{
    color[r][c] = 1;
    d[r][c] = 1;
    out[r][c] = min(out[r][c], d[r][c]);

    queue &lt;int&gt; Qr;
    queue &lt;int&gt; Qc;
    Qr.push(r);
    Qc.push(c);
    while(!Qr.empty())
    {
        int ur = Qr.front();
        int uc = Qc.front();
        Qr.pop();
        Qc.pop();
        for (int i = 0; i &lt; 4; i++)
        {
            int vr = ur + dir[i][0];
            int vc = uc + dir[i][1];
            if (vr &lt; 0 || vr &gt; lines-1 || vc &lt; 0 || vc &gt; width-1) continue;
            if (maze[(ur+vr)/2][(uc+vc)/2] != ' ') continue;
            if (color[vr][vc] == 0)
            {
                color[vr][vc] = 1;
                d[vr][vc] = d[ur][uc] + 1;
                out[vr][vc] = min(out[vr][vc], d[vr][vc]);
                Qr.push(vr);
                Qc.push(vc);
            }
        }
    }
}

int main()
{
    fout.open(&quot;maze1.out&quot;);
    fin.open(&quot;maze1.in&quot;);
    fin &gt;&gt; W &gt;&gt; H;
    lines = 2*H+1;
    width = 2*W+1;
    string temp;
    getline(fin, temp); // read the \n
    for (int i = 0; i &lt; lines; i++)
    {
        getline(fin, maze[i]);
    }
    memset(out, 0x25252525, sizeof(out));
    for (int i = 1; i &lt; width; i += 2)
    {
        if (maze[0][i] == ' ') // N
        {
            memset(color, 0, sizeof(color));
            memset(d, 0, sizeof(d));
            bfs(1, i);
        }
        if (maze[lines-1][i] == ' ') // S
        {
            memset(color, 0, sizeof(color));
            memset(d, 0, sizeof(d));
            bfs(lines-2, i);
        }
    }
    for (int i = 1; i &lt; lines; i += 2)
    {
        if (maze[i][0] == ' ') // W 
        {
            memset(color, 0, sizeof(color));
            memset(d, 0, sizeof(d));
            bfs(i, 1);
        }
        if (maze[i][width-1] == ' ') // E
        {
            memset(color, 0, sizeof(color));
            memset(d, 0, sizeof(d));
            bfs(i, width-2);
        }
    }
    int res = 0;
    for (int i = 1; i &lt; lines; i += 2)
    {
        for (int j = 1; j &lt; width; j += 2)
        {
            res = max(res, out[i][j]);
        }
    }
    fout &lt;&lt; res &lt;&lt; endl;
    return 0;
}
[/c]

标程：
[c]
#include &lt;stdio.h&gt;
#include &lt;stdlib.h&gt;
#include &lt;string.h&gt;
#include &lt;assert.h&gt;

#define MAXWID 38
#define MAXHT 100

typedef struct Point Point;
struct Point {
	int r, c;
};

int wid, ht;
char maze[MAXHT*2+1][MAXWID*2+1+2];	/* extra +2 for &quot;\n&#92;&#48;&quot; */
int dist[MAXHT*2+1][MAXWID*2+1];

Point
Pt(int r, int c)
{
    Point p;

    p.r = r;
    p.c = c;
    return p;
}

typedef struct Queue Queue;
struct Queue {
    Point p;
    int d;
};

Queue floodq[MAXHT*MAXWID];
int bq, eq;

/* if no wall between point p and point np, add np to queue with distance d+1 */
void
addqueue(int d, Point p, Point np)
{
    if(maze[(p.r+np.r)/2][(p.c+np.c)/2] == ' ' &amp;&amp; maze[np.r][np.c] == ' ') {
	maze[np.r][np.c] = '*';
	floodq[eq].p = np;
	floodq[eq].d = d+1;
	eq++;
    }
}

/* if there is an exit at point exitp, plug it and record a start point
 * at startp */

void
lookexit(Point exitp, Point startp)
{
    if(maze[exitp.r][exitp.c] == ' ') {
	addqueue(0, startp, startp);
	maze[exitp.r][exitp.c] = '#';
    }
}

void
main(void)
{
    FILE *fin, *fout;
    Point p;
    int i, r, c, m, d;

    fin = fopen(&quot;maze1.in&quot;, &quot;r&quot;);
    fout = fopen(&quot;maze1.out&quot;, &quot;w&quot;);
    assert(fin != NULL &amp;&amp; fout != NULL);

    fscanf(fin, &quot;%d %d\n&quot;, &amp;wid, &amp;ht);
    wid = 2*wid+1;
    ht = 2*ht+1;

    for(i=0; i&lt;ht; i++)
	fgets(maze[i], sizeof(maze[i]), fin);

    /* find exits */
    for(i=1; i&lt;wid; i+=2) {
	lookexit(Pt(0, i), Pt(1, i));
	lookexit(Pt(ht-1, i), Pt(ht-2, i));
    }
    for(i=1; i&lt;ht; i+=2) {
	lookexit(Pt(i, 0), Pt(i, 1));
	lookexit(Pt(i, wid-1), Pt(i, wid-2));
    }

    /* must have found at least one square with an exit */
    /* since two exits might open onto the same square, perhaps eq == 1 */
    assert(eq == 1 || eq == 2);	

    for(bq = 0; bq &lt; eq; bq++) {
	p = floodq[bq].p;
	d = floodq[bq].d;
	dist[p.r][p.c] = d;

	addqueue(d, p, Pt(p.r-2, p.c));
	addqueue(d, p, Pt(p.r+2, p.c));
	addqueue(d, p, Pt(p.r, p.c-2));
	addqueue(d, p, Pt(p.r, p.c+2));
    }

    /* find maximum distance */
    m = 0;
    for(r=0; r&lt;ht; r++)
    for(c=0; c&lt;wid; c++)
	if(dist[r][c] &gt; m)
	    m = dist[r][c];

    fprintf(fout, &quot;%d\n&quot;, m);

    exit(0);
}
[/c]

第一次看到这个题的时候，感觉好麻烦噢，不过把思路理清之后还好啦。这次使用了之前TEXT Winning Solutions中提到的先写注释再敲代码的方法，好像还真有点用，忽忽。

代码：<!--more-->

[c]
/*
ID: lmy0525
PROG: cowtour
LANG: C++
*/
#include &lt;iostream&gt;
#include &lt;fstream&gt;
#include &lt;string&gt;
#include &lt;algorithm&gt;
#include &lt;cmath&gt;
#include &lt;vector&gt;
#include &lt;cstring&gt;
#include &lt;iomanip&gt;

using namespace std;

ofstream fout;
ifstream fin;

#define INF 1e9

typedef struct Points
{
    int x;
    int y;
}Points;

int N;
Points pts[150];
string matrix[150];
double dist[150][150];
int color[150];
double farest[150];
double diam[150];

double getdist(Points p1, Points p2)
{
    return sqrt((p1.x-p2.x)*(p1.x-p2.x) + (p1.y-p2.y)*(p1.y-p2.y));
}

void mark(int n, int c)
{
    color[n] = c;
    for (int i = 0; i &lt; N; i++)
    {
        if (dist[n][i] != INF &amp;&amp; color[i] == 0) mark(i, c);
    }
}

int main()
{
    fout.open(&quot;cowtour.out&quot;);
    fin.open(&quot;cowtour.in&quot;);
    // input
    fin &gt;&gt; N;
    for (int i = 0; i &lt; N; i++)
    {
        fin &gt;&gt; pts[i].x &gt;&gt; pts[i].y;
    }
    for (int i = 0; i &lt; N; i++)
    {
        fin &gt;&gt; matrix[i];
    }
    // precalc
    for (int i = 0; i &lt; N; i++)
    {
        for (int j = 0; j &lt; N; j++)
        {
            if (i == j)
            {
                dist[i][j] = 0;
            }
            else if (matrix[i][j] == '0')
            {
                dist[i][j] = INF;
            }
            else if (matrix[i][j] == '1')
            {
                dist[i][j] = getdist(pts[i], pts[j]);
            }
        }
    }
    // floyd
    for (int k = 0; k &lt; N; k++)
    for (int i = 0; i &lt; N; i++)
    for (int j = 0; j &lt; N; j++)
    {
        if (dist[i][k] + dist[k][j] &lt; dist[i][j])
            dist[i][j] = dist[i][k] + dist[k][j];
    }
    // mark
    int cur = 1;
    for (int i = 0; i &lt; N; i++)
    {
        if (color[i] == 0) mark(i, cur++);
    }
    // diam &amp; farest
    for (int i = 0; i &lt; N; i++)
    {
        for (int j = 0; j &lt; N; j++)
        {
             if (i == j) continue;
             if (dist[i][j] != INF)
             {
                 farest[i] = max(farest[i], dist[i][j]);
                 diam[color[i]] = max(diam[color[i]], dist[i][j]);
             }
        }
    }
    double res = 1e9;    
    // connect
    for (int i = 0; i &lt; N; i++)
    {
        for (int j = 0; j &lt; N; j++)
        {
            if (color[i] != color[j])
            {
                double newdiam = max(farest[i]+farest[j]+getdist(pts[i], pts[j]), max(diam[color[i]], diam[color[j]]));
                res = min(res, newdiam);
            }
        }
    }
    fout &lt;&lt; setprecision(6) &lt;&lt; fixed &lt;&lt; res &lt;&lt; endl;
    return 0;
}
[/c]

标程：
[c]
#include &lt;stdio.h&gt;
#include &lt;stdlib.h&gt;
#include &lt;string.h&gt;
#include &lt;assert.h&gt;
#include &lt;math.h&gt;

#define INF    (1e40)

typedef struct Point Point;
struct Point {
    int x, y;
};

#define MAXPASTURE 150

int n;
double dist[MAXPASTURE][MAXPASTURE];
double diam[MAXPASTURE];
double fielddiam[MAXPASTURE];
Point pt[MAXPASTURE];
int field[MAXPASTURE];
int nfield;

double
ptdist(Point a, Point b)
{
    return sqrt((double)(b.x-a.x)*(b.x-a.x)+(double)(b.y-a.y)*(b.y-a.y));
}

/* mark the field containing pasture i with number m */
void
mark(int i, int m)
{
    int j;
    if(field[i] != -1) {
        assert(field[i] == m);
        return;
    }

    field[i] = m;
    for(j=0; j&lt;n; j++)
        if(dist[i][j] &lt; INF/2)
            mark(j, m);
}

void
main(void)
{
    FILE *fin, *fout;
    int i, j, k, c;
    double newdiam, d;

    fin = fopen(&quot;cowtour.in&quot;, &quot;r&quot;);
    fout = fopen(&quot;cowtour.out&quot;, &quot;w&quot;);
    assert(fin != NULL &amp;&amp; fout != NULL);

    fscanf(fin, &quot;%d\n&quot;, &amp;n);
    for(i=0; i&lt;n; i++)
        fscanf(fin, &quot;%d %d\n&quot;, &amp;pt[i].x, &amp;pt[i].y);

    for(i=0; i&lt;n; i++) {
        for(j=0; j&lt;n; j++) {
            c = getc(fin);
            if(i == j)
                dist[i][j] = 0;
            else if(c == '0')
                dist[i][j] = INF;        /* a lot */
            else
                dist[i][j] = ptdist(pt[i], pt[j]);
        }
        assert(getc(fin) == '\n');
    }

    /* Floyd-Warshall all pairs shortest paths */
    for(k=0; k&lt;n; k++)
    for(i=0; i&lt;n; i++)
    for(j=0; j&lt;n; j++)
        if(dist[i][k]+dist[k][j] &lt; dist[i][j])
            dist[i][j] = dist[i][k]+dist[k][j];

    /* mark fields */
    for(i=0; i&lt;n; i++)
        field[i] = -1;
    for(i=0; i&lt;n; i++)
        if(field[i] == -1)
            mark(i, nfield++);

    /* find worst diameters involving pasture i, and for whole field */
    for(i=0; i&lt;n; i++) {
        for(j=0; j&lt;n; j++)
            if(diam[i] &lt; dist[i][j] &amp;&amp; dist[i][j] &lt; INF/2)
                diam[i] = dist[i][j];
        if(diam[i] &gt; fielddiam[field[i]])
            fielddiam[field[i]] = diam[i];
    }

    /* consider a new path between i and j */
    newdiam = INF;
    for(i=0; i&lt;n; i++)
    for(j=0; j&lt;n; j++) {
        if(field[i] == field[j])
            continue;

        d = diam[i]+diam[j]+ptdist(pt[i], pt[j]);
        if(d &lt; fielddiam[field[i]])
            d = fielddiam[field[i]];
        if(d &lt; fielddiam[field[j]])
            d = fielddiam[field[j]];

        if(d &lt; newdiam)
            newdiam = d;
    }

    fprintf(fout, &quot;%.6lf\n&quot;, newdiam);
    exit(0);
}
[/c]

Floyd敲起来方便，所以继续用。因为只有50几个顶点。

代码：<!--more-->
[c]
/*
ID: lmy0525
PROG: comehome
LANG: C++
*/
#include &lt;iostream&gt;
#include &lt;fstream&gt;
#include &lt;string&gt;
#include &lt;algorithm&gt;
#include &lt;cmath&gt;
#include &lt;vector&gt;
#include &lt;cstring&gt;
#include &lt;iomanip&gt;
using namespace std;

ofstream fout;
ifstream fin;

int P;
int dist[52][52];

int tonum(char c)
{
    if (isupper(c))
        return c-'A';
    else
        return c-'a'+26;
}

int main()
{
    fout.open(&quot;comehome.out&quot;);
    fin.open(&quot;comehome.in&quot;);
    fin &gt;&gt; P;
    memset (dist, 0x25252525, sizeof(dist));

    for (int i = 0; i &lt; 52; i++) dist[i][i] = 0;
    for (int i = 0; i &lt; P; i++)
    {
        char c1, c2;
        int d;
        fin &gt;&gt; c1 &gt;&gt; c2 &gt;&gt; d;
        int a1 = tonum(c1);
        int a2 = tonum(c2);
        dist[a1][a2] = dist[a2][a1] = min(d, dist[a1][a2]);
    }

    for (int k = 0; k &lt; 52; k++)
    for (int i = 0; i &lt; 52; i++)
    for (int j = 0; j &lt; 52; j++)
    {
        if (dist[i][k] + dist[k][j] &lt; dist[i][j])
            dist[i][j] = dist[i][k] + dist[k][j];
    }

    char firstc;
    int firstd = 0x25252525;
    for (int i = 0; i &lt; 25; i++)
    {
        if (dist[i][25] &lt; firstd)
        {
            firstd = dist[i][25];
            firstc = i+'A';
        }
    }
    fout &lt;&lt; firstc &lt;&lt; &quot; &quot; &lt;&lt; firstd &lt;&lt; endl;
    return 0;
}
