标程：
[c]
#include &lt;stdio.h&gt;
#include &lt;stdlib.h&gt;
#include &lt;string.h&gt;
#include &lt;assert.h&gt;
#include &lt;ctype.h&gt;

#define INF 60000	/* bigger than longest possible path */

int dist[52][52];

int
char2num(char c)
{
    assert(isalpha(c));

    if(isupper(c))
	return c-'A';
    else
	return c-'a'+26;
}

void
main(void)
{
    FILE *fin, *fout;
    int i, j, k, npath, d;
    char a, b;
    int m;

    fin = fopen(&quot;comehome.in&quot;, &quot;r&quot;);
    fout = fopen(&quot;comehome.out&quot;, &quot;w&quot;);
    assert(fin != NULL &amp;&amp; fout != NULL);

    for(i=0; i&lt;52; i++)
    for(j=0; j&lt;52; j++)
	dist[i][j] = INF;

    for(i=0; i&lt;26; i++)
	dist[i][i] = 0;

    fscanf(fin, &quot;%d\n&quot;, &amp;npath);
    for(i=0; i&lt;npath; i++) {
	fscanf(fin, &quot;%c %c %d\n&quot;, &amp;a, &amp;b, &amp;d);
	a = char2num(a);
	b = char2num(b);
	if(dist[a][b] &gt; d)
	    dist[a][b] = dist[b][a] = d;
    }

    /* floyd warshall all pair shortest path */
    for(k=0; k&lt;52; k++)
    for(i=0; i&lt;52; i++)
    for(j=0; j&lt;52; j++)
	if(dist[i][k]+dist[k][j] &lt; dist[i][j])
	    dist[i][j] = dist[i][k]+dist[k][j];

    /* find closest cow */
    m = INF;
    a = '#';
    for(i='A'; i&lt;='Y'; i++) {
	d = dist[char2num(i)][char2num('Z')];
	if(d &lt; m) {
	    m = d;
	    a = i;
	}
    }

    fprintf(fout, &quot;%c %d\n&quot;, a, m);
    exit(0);
}
[/c]

标程（dijstra）：
[pascal]
Var Dist:Array [1..58] of LongInt;      {Array with distances to barn}
    Vis :Array [1..58] of Boolean;      {Array keeping track which
pastures visited}
    Conn:Array [1..58,1..58] of Word;   {Matrix with length of edges, 0 = no edge}

Procedure Load;
Var TF   :Text;
    X,D,E:Word;
    P1,P2:Char;

Begin
 Assign(TF,'comehome.in');
 Reset(TF);
 Readln(TF,E);                          {Read number of edges}
 For X:=1 to E do
 Begin
  Read(TF,P1);                          {Read both pastures and edge
length}
  Read(TF,P2);
  Read(TF,P2);      {Add edge in matrix if no edge between P1 and P2 yet or}
  Readln(TF,D);     {this edge is shorter than the shortest till now}
  If (Conn[Ord(P1)-Ord('A')+1,Ord(P2)-Ord('A')+1]=0) or
     (Conn[Ord(P1)-Ord('A')+1,Ord(P2)-Ord('A')+1]&gt;D) then
  Begin
   Conn[Ord(P1)-Ord('A')+1,Ord(P2)-Ord('A')+1]:=D;
   Conn[Ord(P2)-Ord('A')+1,Ord(P1)-Ord('A')+1]:=D;
  End;
 End;
 Close(TF);
 For X:=1 to 58 do
  Dist[X]:=2147483647;                  {Set all distances to infinity}
 Dist[Ord('Z')-Ord('A')+1]:=0;          {Set distance from barn to barn to 0}
End;

Procedure Solve;
Var X,P,D:LongInt;                      {P = pasture and D = distance}

Begin
 Repeat
  P:=0;
  D:=2147483647;
  For X:=1 to 58 do                     {Find nearest pasture not
visited yet}
   If Not Vis[X] and (Dist[X]&lt;D) then
   Begin
    P:=X;
    D:=Dist[X];
   End;
  If (P&lt;&gt;0) then
  Begin
   Vis[P]:=True;                        {If there is one mark it
visited}
   For X:=1 to 58 do                    {And update all distances}
    If (Conn[P,X]&lt;&gt;0) and (Dist[X]&gt;Dist[P]+Conn[P,X]) then
     Dist[X]:=Dist[P]+Conn[P,X];
  End;
 Until (P=0);                {Until no reachable and unvisited pastures
left}
End;

Procedure Save;
Var TF  :Text;
    X,BD:LongInt;                       {BD = best distance}
    BP  :Char;                          {BP = best pasture}

Begin
 BD:=2147483647;
 For X:=1 to 25 do                      {Find neares pasture}
  If (Dist[X]&lt;BD) then
  Begin
   BD:=Dist[X];
   BP:=Chr(Ord('A')+X-1);
  End;
 Assign(TF,'comehome.out');
 Rewrite(TF);
 Writeln(TF,BP,' ',BD);                 {Write outcome to disk}
 Close(TF);
End;

Begin
 Load;
 Solve;
 Save;
End.
[/pascal]

MST的“处女作”

代码：<!--more-->

[c]
/*
ID: lmy0525
PROG: agrinet
LANG: C++
*/
#include &lt;iostream&gt;
#include &lt;fstream&gt;
#include &lt;string&gt;
#include &lt;algorithm&gt;
#include &lt;cmath&gt;
#include &lt;vector&gt;
#include &lt;cstring&gt;
#include &lt;iomanip&gt;
#include &lt;queue&gt;
using namespace std;

ofstream fout;
ifstream fin;

int weight[100][100];
int parent[100];
int key[100];
int intree[100];
int N;

int mst_prim(int r)
{
    int res = 0;
    for (int i = 0; i &lt; N; i++) key[i] = 1e9;
    key[r] = 0;
    priority_queue &lt; pair&lt;int, int&gt; &gt; Q;
    Q.push(make_pair(-key[r], r));
    while(!Q.empty())
    {
        int u = Q.top().second;
        Q.pop();
        if (intree[u]) continue;
        intree[u] = true;
        res += key[u];
        // cout &lt;&lt; u &lt;&lt; &quot; &quot; &lt;&lt; key[u] &lt;&lt; endl;
        for (int i = 0; i &lt; N; i++)
        {
            if (!intree[i] &amp;&amp; weight[u][i] &lt; key[i])
            {
                key[i] = weight[u][i];
                parent[i] = u;
                Q.push(make_pair(-key[i], i));
            }
        }
    }
    return res;
}

int main()
{
    fout.open(&quot;agrinet.out&quot;);
    fin.open(&quot;agrinet.in&quot;);
    fin &gt;&gt; N;
    for (int i = 0; i &lt; N; i++)
    {
        for (int j = 0; j &lt; N; j++)
        {
            fin &gt;&gt; weight[i][j];
        }
    }
    fout &lt;&lt; mst_prim(0) &lt;&lt; endl;
    return 0;
}
[/c]

标程1：
[c]
#include &lt;stdio.h&gt;
#include &lt;stdlib.h&gt;
#include &lt;string.h&gt;
#include &lt;assert.h&gt;

#define MAXFARM	100

int nfarm;
int dist[MAXFARM][MAXFARM];
int isconn[MAXFARM];

void
main(void)
{
    FILE *fin, *fout;
    int i, j, nfarm, nnode, mindist, minnode, total;

    fin = fopen(&quot;agrinet.in&quot;, &quot;r&quot;);
    fout = fopen(&quot;agrinet.out&quot;, &quot;w&quot;);
    assert(fin != NULL &amp;&amp; fout != NULL);

    fscanf(fin, &quot;%d&quot;, &amp;nfarm);
    for(i=0; i&lt;nfarm; i++)
    for(j=0; j&lt;nfarm; j++) 
	fscanf(fin, &quot;%d&quot;, &amp;dist[i][j]);

    total = 0;
    isconn[0] = 1;
    nnode = 1;
    for(isconn[0]=1, nnode=1; nnode &lt; nfarm; nnode++) {
	mindist = 0;
	for(i=0; i&lt;nfarm; i++)
	for(j=0; j&lt;nfarm; j++) {
	    if(dist[i][j] &amp;&amp; isconn[i] &amp;&amp; !isconn[j]) {
	    	if(mindist == 0 || dist[i][j] &lt; mindist) {
		    mindist = dist[i][j];
		    minnode = j;
		}
	    }
	}
	assert(mindist != 0);

	isconn[minnode] = 1;
	total += mindist;
    }

    fprintf(fout, &quot;%d\n&quot;, total);

    exit(0);
}
[/c]

标程2：
[c]
#include &lt;fstream.h&gt;
#include &lt;assert.h&gt;

const int BIG = 20000000;

int     n;
int     dist[1000][1000];
int     distToTree[1000];
bool    inTree[1000];

main ()
{
    ifstream filein (&quot;agrinet.in&quot;);
    filein &gt;&gt; n;
    for (int i = 0; i &lt; n; ++i) {
	for (int j = 0; j &lt; n; ++j) {
	    filein &gt;&gt; dist[i][j];
	}
	distToTree[i] = BIG;
	inTree[i] = false;
    }
    filein.close ();

    int     cost = 0;
    distToTree[0] = 0;

    for (int i = 0; i &lt; n; ++i) {
	int     best = -1;
	for (int j = 0; j &lt; n; ++j) {
	    if (!inTree[j]) {
		if (best == -1 || distToTree[best] &gt; distToTree[j]) {
		    best = j;
		}
	    }
	}
	assert (best != -1);
	assert (!inTree[best]);
	assert (distToTree[best] &lt; BIG);

	inTree[best] = true;
	cost += distToTree[best];
	distToTree[best] = 0;
	for (int j = 0; j &lt; n; ++j) {
	    if (distToTree[j] &gt; dist[best][j]) {
		distToTree[j] = dist[best][j];
		assert (!inTree[j]);
	    }
	}
    }
    ofstream fileout (&quot;agrinet.out&quot;);
    fileout &lt;&lt; cost &lt;&lt; endl;
    fileout.close ();
    exit (0);
}
[/c]

发现是个背包问题，想起昨天迅哥问我：背包九讲看了没有？我说还木有。于是今天就碰到背包问题了，去看了背包九讲个前2讲，于是发现这个是个完全背包问题，于是就敲了。

代码：<!--more-->
[c]
/*
ID: lmy0525
PROG: inflate
LANG: C++
*/
#include &lt;iostream&gt;
#include &lt;fstream&gt;
#include &lt;string&gt;
#include &lt;algorithm&gt;
#include &lt;cmath&gt;
#include &lt;vector&gt;
#include &lt;cstring&gt;
#include &lt;iomanip&gt;
#include &lt;queue&gt;
using namespace std;

ofstream fout;
ifstream fin;

int weight[10000];
int cost[10000];
int f[10000];

int main()
{
    fout.open(&quot;inflate.out&quot;);
    fin.open(&quot;inflate.in&quot;);
    int M, N;
    fin &gt;&gt; M &gt;&gt; N;
    for (int i = 0; i &lt; N; i++)
    {
        fin &gt;&gt; weight[i] &gt;&gt; cost[i];
    }

    for (int i = 0; i &lt; N; i++)
    {
        for (int j = cost[i]; j &lt;= M; j++)
        {
            f[j] = max(f[j], f[j-cost[i]]+weight[i]);
        }
    }
    fout &lt;&lt; f[M] &lt;&lt; endl;
    return 0;
}
