标程1：
[c]
#include &lt;stdio.h&gt;
#include &lt;stdlib.h&gt;
#include &lt;string.h&gt;

FILE *fp,*fo;

struct rect
{
    int c;
    int x1,y1,x2,y2;
};

int c[2501];
rect r[10001];

int intersect(rect a,const rect &amp;b,rect out[4])
{
    /* do they at all intersect? */
    if(b.x2&lt;a.x1||b.x1&gt;=a.x2)
        return 0;
    if(b.y2&lt;a.y1||b.y1&gt;=a.y2)
        return 0;
    /* they do */

    rect t;

    if(b.x1&lt;=a.x1&amp;&amp;b.x2&gt;=a.x2&amp;&amp;b.y1&lt;=a.y1&amp;&amp;b.y2&gt;=a.y2)
            return -1;

    /* cutting `a' down to match b */
    int nout=0;
    if(b.x1&gt;=a.x1) {
        t=a,t.x2=b.x1;
        if(t.x1!=t.x2)
            out[nout++]=t;
        a.x1=b.x1;
    }
    if(b.x2&lt;a.x2) {
        t=a,t.x1=b.x2;
        if(t.x1!=t.x2)
            out[nout++]=t;
        a.x2=b.x2;
    }
    if(b.y1&gt;=a.y1) {
        t=a,t.y2=b.y1;
        if(t.y1!=t.y2)
            out[nout++]=t;
        a.y1=b.y1;
    }
    if(b.y2&lt;a.y2) {
        t=a,t.y1=b.y2;
        if(t.y1!=t.y2)
            out[nout++]=t;
        a.y2=b.y2;
    }
    return nout;
}

int main(void) {
    fp=fopen(&quot;rect1.in&quot;,&quot;rt&quot;);
    fo=fopen(&quot;rect1.out&quot;,&quot;wt&quot;);

    int a,b,n;
    fscanf(fp,&quot;%d %d %d&quot;,&amp;a,&amp;b,&amp;n);

    r[0].c=1;
    r[0].x1=r[0].y1=0;
    r[0].x2=a;
    r[0].y2=b;

    rect t[4];

    int i,j,rr=1;
    for(i=0;i&lt;n;i++) {
        int tmp;
        fscanf(fp,&quot;%d %d %d %d %d&quot;,&amp;r[rr].x1,&amp;r[rr].y1,&amp;r[rr].x2,&amp;r[rr].y2,&amp;r[rr].c);

        if(r[rr].x1&gt;r[rr].x2) {
            tmp=r[rr].x1;
            r[rr].x1=r[rr].x2;
            r[rr].x2=tmp;
        }
        if(r[rr].y1&gt;r[rr].y2) {
            tmp=r[rr].y1;
            r[rr].y1=r[rr].y2;
            r[rr].y2=tmp;
        }

        int nr=rr;
        rect curr=r[rr++];
        for(j=0;j&lt;nr;j++) {
            int n=intersect(r[j],curr,t);
            if(!n)
                continue;
            if(n==-1) {
                memmove(r+j,r+j+1,sizeof(rect)*(rr-j-1));
                j--;
                rr--;
                nr--;
                continue;
            }
            r[j]=t[--n];
            for(;n--&gt;0;)
                r[rr++]=t[n];
        }
    }

    for(i=0;i&lt;rr;i++)
        c[r[i].c]+=(r[i].x2-r[i].x1)*(r[i].y2-r[i].y1);

    for(i=1;i&lt;=2500;i++)
        if(c[i])
            fprintf(fo,&quot;%d %d\n&quot;,i,c[i]);

    return 0;
}
