可能太久没写程序了，BFS都写了这么久，刚开始准备开8^7的数组，但是总是超内存。于是用了传说中的康托展开。

用了在这里找到的encode/decode代码：<a href="http://apps.topcoder.com/forums/?module=Thread&amp;threadID=579656">http://apps.topcoder.com/forums/?module=Thread&amp;threadID=579656</a>

代码：<!--more-->

[c]
/*
ID: lmy0525
PROG: msquare
LANG: C++
*/
#include &lt;iostream&gt;
#include &lt;fstream&gt;
#include &lt;string&gt;
#include &lt;algorithm&gt;
#include &lt;cmath&gt;
#include &lt;vector&gt;
#include &lt;cstring&gt;
#include &lt;iomanip&gt;
#include &lt;queue&gt;
#include &lt;map&gt;
using namespace std;

ofstream fout;
ifstream fin;

vector&lt;int&gt; init(8);
vector&lt;int&gt; target(8);
int color[40320];
int d[40320];
char op[40320];
int p[40320];
vector &lt;int&gt; v(8);

void transA(vector&lt;int&gt; u)
{
    for (int i = 0; i &lt; 8; i++)
        v[i] = u[7-i];
}

void transB(vector&lt;int&gt; u)
{
    v[0] = u[3];
    v[1] = u[0];
    v[2] = u[1];
    v[3] = u[2];
    v[4] = u[5];
    v[5] = u[6];
    v[6] = u[7];
    v[7] = u[4];
}

void transC(vector&lt;int&gt; u)
{
    v[0] = u[0];
    v[1] = u[6];
    v[2] = u[1];
    v[3] = u[3];
    v[4] = u[4];
    v[5] = u[2];
    v[6] = u[5];
    v[7] = u[7];
}

bool same(vector&lt;int&gt; t, vector&lt;int&gt; u)
{
    for (int i = 0; i &lt; 8; i++)
        if (t[i] != u[i]) return false;
    return true;   
}

int prod[8] = {5040, 720, 120, 24, 6, 2, 1, 1};

vector &lt;int&gt; decode(int mask)
{
    vector &lt;int&gt; ret(8), t = init;
    int u, v;
    for(u = 0; u &lt; 8; u++)
    {
        v = mask / prod[u];
        ret[u] = t[v];
        t.erase(t.begin() + v);
        mask -= v * prod[u];
    }
    return ret;
}

int encode(vector &lt;int&gt; num)
{
    int u, v, mask = 0;
    vector &lt;int&gt; t = init;
    for(u = 0; u &lt; 8; u++)
    {
        for(v = 0; t[v] != num[u]; v++);
        mask += v * prod[u];
        t.erase(t.begin() + v);
    }
    return mask;
}

void bfs(vector&lt;int&gt; s)
{
    int temp = encode(s);
    color[temp] = 1;
    d[temp] = 0;
    p[temp] = -1;
    queue &lt; vector&lt;int&gt; &gt; Q;
    Q.push(s);
    while(!s.empty())
    {
        vector &lt;int&gt; u = Q.front();
        Q.pop();
        int tempu = encode(u);
        if (same(target, u))
        {
            temp = tempu;
            fout &lt;&lt; (int)d[temp] &lt;&lt; endl;
            string res = &quot;&quot;;
            while(p[temp] != -1)
            {
                res = op[temp] + res;
                u = decode(p[temp]);
                temp = encode(u);
            }
            int i;
            for (i = 0; i &lt; res.size(); i++)
            {
                if (i != 0 &amp;&amp; i % 60 == 0) fout &lt;&lt; endl;
                fout &lt;&lt; res[i];
            }
            fout &lt;&lt; endl;
            return;
        }
        transA(u);
        int tempv = encode(v);
        if (color[tempv] == 0)
        {
            color[tempv] = 1;
            d[tempv] = d[tempu] + 1;
            op[tempv] = 'A';
            p[tempv] = tempu;
            Q.push(v);
        }
        transB(u);
        tempv = encode(v);
        if (color[tempv] == 0)
        {
            color[tempv] = 1;
            d[tempv] = d[tempu] + 1;
            op[tempv] = 'B';
            p[tempv] = tempu;
            Q.push(v);
        }
        transC(u);
        tempv = encode(v);
        if (color[tempv] == 0)
        {
            color[tempv] = 1;
            d[tempv] = d[tempu] + 1;
            op[tempv] = 'C';
            p[tempv] = tempu;
            Q.push(v);
        }
    }
}

int main()
{
    fout.open(&quot;msquare.out&quot;);
    fin.open(&quot;msquare.in&quot;);
    for (int i = 0; i &lt; 8; i++)
        fin &gt;&gt; target[i];
    for (int i = 0; i &lt; 8; i++)
        init[i] = i+1;
    bfs(init);
    return 0;
}
