[c]
/*
ID: lmy0525
PROG: fence
LANG: C++
*/
#include &lt;iostream&gt;
#include &lt;fstream&gt;
#include &lt;string&gt;
#include &lt;algorithm&gt;
#include &lt;cmath&gt;
#include &lt;vector&gt;
#include &lt;cstring&gt;
#include &lt;iomanip&gt;
#include &lt;queue&gt;
#include &lt;map&gt;
#include &lt;stack&gt;
using namespace std;

ofstream fout;
ifstream fin;

int mat[501][501];
int degree[501];
stack &lt;int&gt; circuit;

void find_circuit(int node)
{
    if (degree[node] == 0)
    {
        circuit.push(node);
    }
    else
    {
        while(degree[node] &gt; 0)
        {
            int i;
            for (i = 1; i &lt;= 500; i++)
            {
                if (mat[node][i] &gt; 0)
                {
                    break;
                }
            }
            mat[node][i]--;
            mat[i][node]--;
            degree[node]--;
            degree[i]--;
            find_circuit(i);
        }
        circuit.push(node);
    }
}

int main()
{
    fout.open(&quot;fence.out&quot;);
    fin.open(&quot;fence.in&quot;);
    int F;
    fin &gt;&gt; F;
    for (int i = 0; i &lt; F; i++)
    {
        int a, b;
        fin &gt;&gt; a &gt;&gt; b;
        mat[a][b]++;
        mat[b][a]++;
        degree[a]++;
        degree[b]++;
    }
    bool done = false;
    for (int i = 1; i &lt;= 500; i++)
    {
        if (degree[i] % 2 == 1)
        {
            find_circuit(i);
            done = true;
            break;
        }
    }
    if (!done)
    {
        for (int i = 1; i &lt;= 500; i++)
        {
            if (degree[i] &gt; 0)
            {
                find_circuit(i);
                break;
            }
        }
    }
    while(!circuit.empty())
    {
        fout &lt;&lt; circuit.top() &lt;&lt; endl;
        circuit.pop();
    }
    return 0;
}
