标程1：

[c]
#include &lt;stdio.h&gt;
#include &lt;stdlib.h&gt;
#include &lt;string.h&gt;
#include &lt;assert.h&gt;

#define MAXN 250

int goodsq[MAXN][MAXN];
int bigsq[MAXN][MAXN];
int tot[MAXN+1];

int
min(int a, int b)
{
    return a &lt; b ? a : b;
}

void
main(void)
{
    FILE *fin, *fout;
    int i, j, k, l, n, sz;

    fin = fopen(&quot;range.in&quot;, &quot;r&quot;);
    fout = fopen(&quot;range.out&quot;, &quot;w&quot;);
    assert(fin != NULL &amp;&amp; fout != NULL);

    fscanf(fin, &quot;%d\n&quot;, &amp;n);

    for(i=0; i&lt;n; i++) {
	for(j=0; j&lt;n; j++)
	    goodsq[i][j] = (getc(fin) == '1');
	assert(getc(fin) == '\n');
    }

    /* calculate size of biggest square with lower right corner (i,j) */
    for(i=0; i&lt;n; i++) {
	for(j=0; j&lt;n; j++) {
	    for(k=i; k&gt;=0; k--)
		if(goodsq[k][j] == 0)
		    break;

	    for(l=j; l&gt;=0; l--)
		if(goodsq[i][l] == 0)
		    break;

	    sz = min(i-k, j-l);
	    if(i &gt; 0 &amp;&amp; j &gt; 0)
		sz = min(sz, bigsq[i-1][j-1]+1);

	    bigsq[i][j] = sz;
	}
    }

    /* now just count squares */
    for(i=0; i&lt;n; i++)
    for(j=0; j&lt;n; j++)
    for(k=2; k&lt;=bigsq[i][j]; k++)
	tot[k]++;

    for(i=2; i&lt;=n; i++)
	if(tot[i])
	    fprintf(fout, &quot;%d %d\n&quot;, i, tot[i]);

    exit(0);
}
